import {useState} from "react";
let name = "akram";
export default function MyButton(){
    console.log("render");
    const[name, setName] = useState("akram")

    function buttonClick(){
        if(name == "messi"){
            setName = "akram";
        }else {
            setName = "messi";
        }
    }

    return(
        <div>
            <button onClick={buttonClick}>Click here </button>
            <h1> {name}</h1>
        </div>
    
    );
}
